from flask_wtf import FlaskFormfrom wtforms import StringField, IntegerFieldfrom wtforms import BooleanField, SubmitFieldfrom wtforms.validators import DataRequiredclass JobsForm(FlaskForm):    title = StringField('Job title', validators=[DataRequired()])    team_leader = IntegerField("Team Leader id")    work_size = IntegerField("Work Size")    collaborators = StringField('Collaborators')    is_private = BooleanField("Is job finished?")    submit = SubmitField('Submit')